// Set a global property
globalPath.setPropertyValue( "myGlobalProp", "10" )
// Set a project property
projectPath.setPropertyValue( "myProjectProp", "20" ) 
// Set a test suite property
testSuitePath.setPropertyValue( "myTestSuiteProp", "30" )
// Set a test case property
testCasePath.setPropertyValue( "myTestCaseProp", "40" )
