projectPath.testSuites["CalculatorTestSuite-1"].testCases["add TestCase"].testSteps["add"].getPropertyValue("Response")

import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
def testSuitePath= testRunner.testCase.testSuite
def xmlReq = testSuitePath.testCases["add TestCase"].testSteps["divide"].getPropertyValue("Request")

def holder = new XmlHolder(xmlReq)

def testCasePath= testRunner.testCase
def testCaseProperty = testCasePath.getPropertyValue("myTestCaseProp")
def testCasePropertyInteger = testCaseProperty.toInteger()

holder.setNodeValue("//exam:divide/intA",testCasePropertyInteger)
holder.setNodeValue("//exam:divide/intB","20")

def total= testCasePropertyInteger/20

def finalXmlReq=holder.getXml()
def xmlReqSet = testSuitePath.testCases["add TestCase"].testSteps["divide"].setPropertyValue("Request",finalXmlReq)

def divideReqTestStep=testRunner.testCase.testSteps["divide"]

def wsdlTestRunObject = new WsdlTestRunContext(divideReqTestStep)

divideReqTestStep.run(testRunner,wsdlTestRunObject)
//------------------------------------------------------------------
def xmlRespo = testSuitePath.testCases["add TestCase"].testSteps["divide"].getPropertyValue("Response")
def responseholder = new XmlHolder(xmlRespo)

def resultValue= responseholder.getNodeValue("//ns2:divideResponse/value")
def finalResultValue= resultValue.toInteger()

//assert total == finalResultValue

if (total == finalResultValue){
	log.info "test Passed"
}
else {
	log.info "teat failed"
}
